import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    language: localStorage.getItem("language") || 'ky' // Get the language from the local storage or set the default language to 'ky'
};

export const generalSlice = createSlice({
    name: 'general',
    initialState,
    reducers: {
        setLanguage: (state, {payload}) => {
            state.language = payload;
            localStorage.setItem("language", payload); // Save the selected language to the local storage
        }
    },
    extraReducers: {
    }
});

export const {
    setLanguage
} = generalSlice.actions;

export default generalSlice;
