import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { useTranslation } from "react-i18next";
import i18next from "i18next";
import { Route, Routes, Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Backbtn from '../images/back-btn.png'
import axios from "axios";
const SelectCountry = () => {

    const [country, setCountry]= useState([]);
    const [countryid, setCountryid]= useState('');
    const [stetes, setSat]= useState([]);
    useEffect( ()=>{
        const getcountry= async ()=>{
          const req= await axios("http://localhost/devopsdeveloper/country");
          const getres= await req.json();
          console.log(getres);
          setCountry(await getres);

        }
        getcountry();


       },[]);
       const handlecountry=(event)=>{
        const getcoutryid= event.target.value;
        setCountryid(getcoutryid);
        event.preventDefault();
      }

      useEffect( ()=>{

        const getstate= async ()=>{
          const resstate= await axios(`http://localhost/devopsdeveloper/state/getstate/${countryid }`);
          const getst= resstate.json();
          setSat(await getst);

        }
        getstate();

      },[countryid]); 
    const { t } = useTranslation();
    const onChangeHandler = (e) => {
        const index = e.target.selectedIndex;
        const el = e.target.childNodes[index]
        const option =  el.getAttribute('id');  
        i18next.changeLanguage(option)
      }

    const {
        register,
        formState: { errors, isValid },
        handleSubmit,
        reset,
        watch,
        getValues,
    } = useForm({ mode: "onChange" });

    const onSubmit = (data) => {
        console.log(JSON.stringify(data));
        reset();
    };
  return (
    <div className="reg">
        <Link to="/">
        <img className='back_btn' src={Backbtn} alt=" back btn" />
      </Link>
    <div className="reg_btns">
        <div className="reg_light"></div>
        <div className="reg_light active"></div>
    </div>

    <form className="reg_form" onSubmit={handleSubmit(onSubmit)}>
        <div className="block1">
        <select name="country" className="form-control" onChange={(e)=>handlecountry(e)}>
                   <option>--Select Country--</option>
                   {
                     country.map( (getcon)=>(
                   <option key={getcon.country_id} value={getcon.country_id }> { getcon.country_name}</option>
                     ))
                }

                 </select>
                 <select name="state" className="form-control">
                   <option>--Select State--</option>
                   {
                     stetes.map( (st,index)=>(                    
                   <option key={index} value={st.state_id}>{ st.state_name}</option>
                     ))
                     }
                 </select>
        </div>



        <div style={{ position: "relative" }}>
            {errors?.password && (
                <p
                    style={{
                        color: "red",
                        position: "absolute",
                        left: "52%",
                    }}
                >
                    {errors?.password?.message || "Error"}
                </p>
            )}
            {errors?.email && (
                <p
                    style={{
                        color: "red",
                        marginTop: "10px",
                        paddingLeft: "15px",
                    }}
                >
                    {errors?.email?.message || "Error"}
                </p>
            )}
        </div>
        <div className="block3">
            <input
                type="password"
                placeholder={t("reg_password_confirm")}
                className="password_confirm"
                {...register("passwordСonfirm", {
                    required: "поле обязательно к заполнению",
                    validate: (val) => {
                        const { password } = getValues();
                        if (watch("password") != val) {
                            return (
                                password == val || "пароли не совпадают"
                            );
                        }
                    },
                })}
            />
        </div>
        <div>
            {" "}
            {errors.passwordСonfirm && (
                <p
                    style={{
                        color: "red",
                        marginTop: "10px",
                        paddingLeft: "15px",
                    }}
                >
                    {errors.passwordСonfirm.message}
                </p>
            )}
        </div>

        <div className="block4">
            <button
                className="reg_submit btn"
                disabled={!isValid}
                type="submit"
            >
                {t("reg_submit")}
            </button>
        </div>

        <div className="block5">
            <p>
                {t("reg_checkReg")}{" "}
                <a
                    style={{
                        textDecoration: "none",
                        color: "#7F3A85",
                        cursor: "pointer",
                    }}
                    href=""
                >
                    {t("reg_login")}
                </a>{" "}
            </p>
        </div>
        <div className="block7">
            <select
                className="select-css"
                name="language"
                id="language"
                onChange={onChangeHandler}
            >
                <option
                    id="ky"
                    value="ky"
                >
                    кыргызча
                </option>

                <option
                 id="ru"
                    value="ru"
                >
                    русский
                </option>
            </select>
        </div>

    </form>
</div>
  )
}

export default SelectCountry
