import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { useTranslation } from "react-i18next";
import i18next from "i18next";
import { useSelector } from "react-redux";
import {Link, useNavigate } from "react-router-dom";
import styles from './register.module.scss'
import { useDispatch } from "react-redux";
import { signupUser } from "../../../../features/auth/authSlice";
import Backbtn from '../../../../assets/images/back-btn.png'
export const Register = ({ toStep }) => {
    const navigate = useNavigate();
    const [successful, setSuccessful] = useState(false);
    const [showSecondForm, setShowSecondForm] = useState(false);


    let dispatch = useDispatch();
    const { t } = useTranslation();
    const [selectedLanguage, setSelectedLanguage] = useState(
        localStorage.getItem("selectedLanguage") || "ky"
      );
      const [selectCountry, setSelectCountry] = useState(null);
    const [selectCity, setSelectCity] = useState(null);

      const { isLoggedIn } = useSelector((state) => state.auth);


      useEffect(() => {
        i18next.changeLanguage(selectedLanguage);
        localStorage.setItem("selectedLanguage", selectedLanguage);
      }, [selectedLanguage]);

      const onChangeLangHandler = (event) => {
        setSelectedLanguage(event.target.value);
      };
    const {
        register,
        formState: { errors, isValid },
        handleSubmit,
        watch,
        getValues,
    } = useForm({

        mode: "onChange",
    });

    const selectedCountry = watch("country");
    const selectedCity = watch("city");
    const onSubmit = (data) => {
        console.log(JSON.stringify(data));
        const { password2 ,first_name,last_name, email, password } = data;
        console.log(data)

        setSuccessful(false);

        dispatch(signupUser( { password2,first_name,last_name, email, password } ))
          .unwrap()
          .then(() => {
            setSuccessful(true);
          })
          .catch(() => {
            setSuccessful(false);
          });
  };


    return (
        (
            <div className={styles.content}>

            <div className={styles.reg}>
                <div className={styles.reg_btns}>
                    <div className={styles.reg_light_active}></div>
                    <div className={styles.reg_light}></div>
                </div>

                <form className={styles.reg_form} onSubmit={handleSubmit(onSubmit)}>
                    {!showSecondForm ? (
          <div className="step1">

          <div className={styles.block1}>
              <input
                  placeholder={t("reg_name")}
                  className={styles.firstname}
                  {...register("first_name", {
                      required: "поле обязательно к заполнению",
                  })}
              />

              <input
                  placeholder={t("reg_surname")}
                  className={styles.lastname}
                  {...register("last_name", {
                      required: "поле обязательно к заполнению",
                  })}
              />
          </div>
          <div style={{ position: "relative" }}>
              {errors?.lastName && (
                  <p
                      style={{
                          color: "red",
                          position: "absolute",
                          left: "52%",
                      }}
                  >
                      {errors?.lastName?.message}
                  </p>
              )}
              {errors?.firstName && (
                  <p
                      style={{
                          color: "red",
                          marginTop: "10px",
                          paddingLeft: "15px",
                      }}
                  >
                      {errors?.firstName?.message}
                  </p>
              )}
          </div>

          <div className={styles.block2}>
              <input
                  placeholder="E-mail"
                  className={styles.email}
                  {...register("email", {
                      required: "поле обязательно к заполнению",
                      pattern: {
                          value: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
                          message: "напишите правильный email",
                      },
                  })}
              />

              <input
                  type="password"
                  placeholder={t("reg_password")}
                  className={styles.password}
                  {...register("password", {
                      required: "поле обязательно к заполнению",
                      minLength: {
                          value: 8,
                          message: "минимум 8 символов",
                      },
                  })}
              />
          </div>
          <div style={{ position: "relative" }}>
              {errors?.password && (
                  <p
                      style={{
                          color: "red",
                          position: "absolute",
                          left: "52%",
                      }}
                  >
                      {errors?.password?.message}
                  </p>
              )}
              {errors?.email && (
                  <p
                      style={{
                          color: "red",
                          marginTop: "10px",
                          paddingLeft: "15px",
                      }}
                  >
                      {errors?.email?.message}
                  </p>
              )}
          </div>
          <div className={styles.block3}>
              <input
