import React from 'react'
import { useForm } from 'react-hook-form';
import {useTranslation } from "react-i18next";


const Content = () => {
  const { t } = useTranslation();

  const {
    register,
    formState: {
        errors,
    },
    handleSubmit,
} = useForm();

const onSubmit = (data) => {
  alert(JSON.stringify(data))
}
  return (
    <div className='registation' >

      <div className='reg_btns'>
      <div className='reg_light active'></div>
      <div className='reg_light active'></div>
      </div>

      <form  className='registation_form'  onSubmit={handleSubmit(onSubmit)}>

        <input placeholder={t('reg_name')} className='firstname' {...register('firstName', {
          required : 'поле обязательно к заполнению'
        })} />
          <input placeholder={t('reg_surname')} className='lastname' {...register('lastName', {
          required : 'поле обязательно к заполнению'
        })}/>
            <input placeholder='e-mail' className='email' {...register('email', {
          required : 'поле обязательно к заполнению'
        })}/>
            <input type='password' placeholder='Пароль' className='password' {...register('password', {
          required : 'поле обязательно к заполнению'
        })}/>
            <input type='password' placeholder='Подтверждение пароля' className='password_confirm' {...register('passwordСonfirm', {
          required : 'поле обязательно к заполнению'
        })}/>
      </form>
    </div>
  )
}

export default Content
