import { createSlice } from "@reduxjs/toolkit";
import { deleteUserById, fetchAllUsers } from "../../api/user";

const FetchUsersSlice = createSlice({
    name: 'users',
    initialState: {
      users: [],
      status: 'idle',
      error: null,
    },
    reducers: {},
    extraReducers: (builder) => {
      builder
        .addCase(fetchAllUsers.pending, (state) => {
          state.status = 'loading';
        })
        .addCase(fetchAllUsers.fulfilled, (state, action) => {
          state.status = 'succeeded';
          state.users = action.payload;
        })
        .addCase(fetchAllUsers.rejected, (state, action) => {
          state.status = 'failed';
          state.error = action.error.message;
        })
        .addCase(deleteUserById.pending, (state) => {
          state.isLoading = true;
          state.error = null;
        })
        .addCase(deleteUserById.fulfilled, (state, action) => {
          state.isLoading = false;
          state.users = state.users.filter((user) => user.id !== action.payload);
        })
        .addCase(deleteUserById.rejected, (state, action) => {
          state.isLoading = false;
          state.error = action.error.message;
        })
        .addCase(updateUserById.pending, (state) => {
          state.status = 'loading';
        })
        .addCase(updateUserById.fulfilled, (state, action) => {
          state.status = 'succeeded';
          // Find the index of the updated user in the users array and replace it
          const updatedUserIndex = state.users.findIndex(user => user.id === action.payload.id);
          if (updatedUserIndex !== -1) {
            state.users[updatedUserIndex] = action.payload;
          }
        })
        .addCase(updateUserById.rejected, (state, action) => {
          state.status = 'failed';
          state.error = action.error.message;
        });

    },
  });

  export const {} = FetchUsersSlice.actions;

  export default FetchUsersSlice;
