import React, {useState,useEffect} from 'react'
import logo from '../../assets/images/logo.png'
import i18next from "i18next";
import { Link } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux';
import { useCallback } from 'react';
import { logout } from '../../features/auth/authSlice';
import { useNavigate } from 'react-router-dom';
import {useTranslation } from "react-i18next";
import styles from './MainHeader.module.scss'
import user from '../../assets/images/user.png'
import { setLanguage } from '../../redux/slices/generalSlice';

const MainHeader = () => {
    const navigate = useNavigate();
    const [open,setOpen] = useState('');
    const { user: currentUser } = useSelector((state) => state.auth);
    const dispatch = useDispatch();

    const logOut = useCallback(() => {
      dispatch(logout());
    }, [dispatch]);

    const { t } = useTranslation();

      const { language } = useSelector(state => state.general);

      const handleLanguageChange = e => {
          dispatch(setLanguage(e.target.value));
      }

      useEffect(() => {
        i18next.changeLanguage(language);
        localStorage.setItem("selectedLanguage", language);
      }, [language]);

  return (
    <div className={styles.main_header}>
        <div  className={`${styles.navbar} ${styles.navbar} ? ${styles.scrolled} : ''}`} >
        <img className={styles.main_logo} src={logo} alt="logo" />

<ul>
<li>
  <Link to={'/'}>{t('main_title')}</Link>
  </li>
<li>
<Link to={'/'}>{t('main_mentor')}</Link>
</li>
<li>
<Link to={'/'}>{t('main_trening')}</Link>
  </li>
<li>
<Link to={'/video'}>{t('main_video')}</Link>

</li>
<li>
<Link to={'/shop'}>{t('main_shop')}</Link>
  </li>
</ul>
<select
                        className={styles.select_lang}
                        name="language"
                        id="language"
                        value={language}
                        onChange={handleLanguageChange}
                    >

                        <option id="ky" value="ky">
                          🇰🇬 кыргызча
                        </option>
                        <option id="ru" value="ru"> 

                        🇷🇺  русский
                        </option>
                    </select>

                      {currentUser ? (
                        <div className={styles.profile_block}>

                          <img onClick={() => {
                            setOpen(!open)
                          }} className={styles.profile_img} src={user}>


                            </img>      
                            {open && (
                              <ul>
                                <li className={styles.dropdown}  >Профиль</li>
                                <li className={styles.dropdown}  >{t('logout')}</li>

                              </ul>
                            )}              
                        </div>
                      ) : (
<button onClick={()=> {
navigate('/login')}} className={styles.main_submit}>{t("reg_submit")}</button>
                      )}


        </div>

    </div>
  )
}

export default MainHeader
