import React from "react";
import { useTranslation } from "react-i18next";

import styles from "./MainContent.module.scss";
import { Link } from "react-scroll";

const MainContent = () => {
    const { t } = useTranslation();

    return (
        <main className={styles.main}>
            <div className={styles.main_container}>
                <div className={styles.main_text}>
                    <h1 className={styles.main_title}>{t("slogan")}</h1>
                    <Link
                        style={{ margin: "0 auto" }}
                        to="about"
                        spy={true}
                        smooth={true}
                        offset={-100}
                        duration={500}
                    >
                        <button className={styles.main_scroll_btn}>
                            {t("main_scroll")}
                        </button>
                    </Link>
                </div>
                <div className={styles.main_img}></div>
                <div className={styles.main_bottom}>
                  <h1  className={styles.main_bottom_title}>Мы верим в расширение прав и возможностей девочек, чтобы они могли взять свою жизнь в свои руки и полностью раскрыть свой потенциал.</h1>
                  <p  className={styles.main_bottom_subtitle}></p>
                </div>
            </div>
        </main>
    );
};

export default MainContent;
