import React from 'react';
import { createSlice, configureStore } from '@reduxjs/toolkit';

const dataSlice = createSlice({
  name: 'data',
  initialState: {},
  reducers: {
    setValues: (state, action) => {
      return { ...state, ...action.payload };
    },
  },
});

export const { setValues } = dataSlice.actions;

export const store = configureStore({
  reducer: dataSlice.reducer,
});

export const useData = () => {
  const data = React.useSelector((state) => state);
  const dispatch = React.useDispatch();
  const setValuesCallback = React.useCallback(
    (values) => dispatch(setValues(values)),
    [dispatch]
  );

  return { data, setValues: setValuesCallback };
};
