import { createSlice } from '@reduxjs/toolkit';
import { fetchAllTranings } from '../../api/tranings';
const initialState = {
    tranings: [],
    status: 'idle',
    error: null,
  };

  export const traningSlice = createSlice({
    name: 'tranings',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
      builder
        .addCase(fetchAllTranings.pending, (state) => {
          state.status = 'loading';
        })
        .addCase(fetchAllTranings.fulfilled, (state, action) => {
          state.status = 'succeeded';
          state.tranings = action.payload;
        })
        .addCase(fetchAllTranings.rejected, (state, action) => {
          state.status = 'failed';
          state.error = action.error.message;
        });
    },
  });


  export default traningSlice;
