        //         <form onSubmit={onFormSubmit}>
        //             <input
        //                 placeholder='Email'
        //                 className={styles.email_input}
        //                 {...emailProps}/>
        //             {errors?.email && (
        //                 <p 
        //                     style={{
        //                         color: "red",
        //                         marginTop: "10px",
        //                         paddingLeft: "15px",
        //                     }}>
        //                         {errors?.email?.message}
        //                 </p>
        //             )}
        //             <input 
        //                 type="password"
        //                 placeholder='Password'
        //                 className={styles.password_input}
        //                 {...passwordProps}/>
        //             <button type="submit">Submit</button>
        //         </form>
