import React, {useState,useEffect} from 'react'
import logo from '../../assets/images/logo.png'
import i18next from "i18next";
import { useDispatch,useSelector } from 'react-redux';
import { useCallback } from 'react';
import { logout } from '../../features/auth/authSlice';
import { useNavigate } from 'react-router-dom';
import {useTranslation } from "react-i18next";
import styles from './MainHeader.module.scss'
import EventBus from '../../common/eventBus';


const MainHeader = () => {
    const navigate = useNavigate();
    const [showModeratorBoard, setShowModeratorBoard] = useState(false);
    const [showAdminBoard, setShowAdminBoard] = useState(false);
    const { user: currentUser } = useSelector((state) => state.auth);
    const dispatch = useDispatch();

    const logOut = useCallback(() => {
      dispatch(logout());
    }, [dispatch]);

    useEffect(() => {
      if (currentUser) {
        setShowModeratorBoard(currentUser.roles.includes("ROLE_MODERATOR"));
        setShowAdminBoard(currentUser.roles.includes("ROLE_ADMIN"));
      } else {
        setShowModeratorBoard(false);
        setShowAdminBoard(false);
      }

      EventBus.on("logout", () => {
        logOut();
      });

      return () => {
        EventBus.remove("logout");
      };
    }, [currentUser, logOut]);


    const { t } = useTranslation();
    const [selectedLanguage, setSelectedLanguage] = useState(
        localStorage.getItem("selectedLanguage") || "ky"
      );

      useEffect(() => {
        i18next.changeLanguage(selectedLanguage);
        localStorage.setItem("selectedLanguage", selectedLanguage);
      }, [selectedLanguage]);

      const onChangeLangHandler = (event) => {
        setSelectedLanguage(event.target.value);
      };


  return (
    <div className={styles.main_header}>
        <div  className={`${styles.navbar} ${styles.navbar} ? ${styles.scrolled} : ''}`} >
        <img className={styles.main_logo} src={logo} alt="logo" />

<ul>
<li><a href="">{t('main_title')}</a></li>
<li><a href=""> {t('main_mentor')}</a></li>
<li><a href="">{t('main_trening')}</a></li>
<li><a href="">{t('main_video')}</a></li>
<li><a href="">{t('main_shop')}</a></li>
</ul>
<select
                        className={styles.select_lang}
                        name="language"
                        id="language"
                        onChange={onChangeLangHandler}
                        value={selectedLanguage}
                    >

                        <option id="ky" value="ky">
                          🇰🇬кыргызча
                        </option>
                        <option id="ru" value="ru"> 

                        🇷🇺  русский
                        </option>
                    </select>
                    <nav>
                      {currentUser && (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/profile"} className="nav-link">
                  {currentUser.username}
                </Link>
              </li>
              <li className="nav-item">
                <a href="/login" className="nav-link" onClick={logOut}>
                  LogOut
                </a>
              </li>
            </div>) }
                    </nav>
<button onClick={()=> {
navigate('/login')}} className={styles.main_submit}>{t("reg_submit")}</button>
        </div>

    </div>
  )
}

export default MainHeader
