import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { useTranslation } from "react-i18next";
import i18next from "i18next";
import { Route, Routes, Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { Country, City, State } from "country-state-city";
import Backbtn from '../images/back-btn.png'
import axios from "axios";
const SelectCountry = () => {
    let countryData = Country.getAllCountries();
    const [stateData, setStateData] = useState();
    const [cityData, setCityData] = useState();

    const [country, setCountry] = useState(countryData[0]);
    const [state, setState] = useState();
    const [city, setCity] = useState();

    useEffect(() => {
      setStateData(State.getStatesOfCountry(country?.isoCode));
    }, [country]);

    useEffect(() => {
      setCityData(City.getCitiesOfState(country?.isoCode, state?.isoCode));
    }, [state]);

    useEffect(() => {
      stateData && setState(stateData[0]);
    }, [stateData]);

    useEffect(() => {
      cityData && setCity(cityData[0]);
    }, [cityData]);
    console.log(countryData)
    const { t } = useTranslation();
    const onChangeHandler = (e) => {
        const index = e.target.selectedIndex;
        const el = e.target.childNodes[index]
        const option =  el.getAttribute('id');  
        i18next.changeLanguage(option)
      }

    const {
        register,
        formState: { errors, isValid },
        handleSubmit,
        reset,
        watch,
        getValues,
    } = useForm({ mode: "onChange" });

    const onSubmit = (data) => {
        console.log(JSON.stringify(data));
        reset();
    };
  return (
    <div className="reg">
        <Link to="/">
        <img className='back_btn' src={Backbtn} alt=" back btn" />
      </Link>
    <div className="reg_btns">
        <div className="reg_light"></div>
        <div className="reg_light active"></div>
    </div>

    <form className="reg_form" onSubmit={handleSubmit(onSubmit)}>
        <div className="block1">
        <select name="country" className="form-control">
        {countryData.map(data =>
      <option key={data.isoCode} value={data.name}>{data.name}</option>
    )};                 </select>

        </div>


        <div className="block2">
        <select name="state" className="form-control">
                   <option>{t('reg_city')}</option>

                 </select>
        </div>
        <div className="block4">
            <button
                className="reg_submit btn"
                disabled={!isValid}
                type="submit"
            >
                {t("reg_submit")}
            </button>
        </div>

        <div className="block5">
            <p>
                {t("reg_checkReg")}{" "}
                <a
                    style={{
                        textDecoration: "none",
                        color: "#7F3A85",
                        cursor: "pointer",
                    }}
                    href=""
                >
                    {t("reg_login")}
                </a>{" "}
            </p>
        </div>
        <div className="block7">
            <select
                className="select-css"
                name="language"
                id="language"
                onChange={onChangeHandler}
            >
                <option
                    id="ky"
                    value="ky"
                >
                    кыргызча
                </option>

                <option
                 id="ru"
                    value="ru"
                >
                    русский
                </option>
            </select>
        </div>

    </form>
</div>
  )
}

export default SelectCountry
