import {useTranslation } from "react-i18next";
import Header from '../../components/Header'
import Log from '../../components/Logg/Log'
import Logo from '../../components/Logo/Logo'
import { useState } from "react";
import '../App.css'
import { Route, Routes, Link } from "react-router-dom";

import Auto from "../../components/Auto/Auto";

function Auth() {
  const { t } = useTranslation();
	const [step, setStep] = useState(1);
	const toStep = (val) => {
		setStep(val);
	};

	const view = () => {
		switch (step) {
			case 1:
				return <FormSignIn toStep={toStep} />;
			case 2:
				return <FormRecoveryWithLogin toStep={toStep} setUserData={setUserData} />;
			case 3:
				return <FormRecoveryWithPass toStep={toStep} userData={userData} />;
			case 4:
				return <FormRecoveryWithNewPass toStep={toStep} />;
			default:
				return;
		}
	};
  return (
    <div className="Login">
      <div className='header'>
      <Header/>
      </div>
      <div className='Logo'>
        <Logo/>
        </div>
        <div className='content'>
				{view()}
        </div>



    </div>
  );
}

export default Auth;
