import styles from './style.module.scss';
import { useTranslation } from "react-i18next";
import {Link} from "react-router-dom";

import Header from '../Header';
import Logo from '../Logo/Logo';
import { useDispatch, useSelector } from 'react-redux';
import { setLanguage } from '../../redux/slices/generalSlice';
import { useEffect, useState } from 'react';
import i18next from 'i18next';
import { fetchData } from '../../api/geo';


export function RegisterView({onFormSubmit,districtProps,regionProps,checkBox,handleLanguageChange,language, emailProps, passwordProps, errors,firstNameProps,lastNameProps,passwordConfirmProps, isValid}) {
    const { t } = useTranslation();
    const geo = useSelector(state => state.data);
    const dispatch = useDispatch();

    const [showSecondForm, setShowSecondForm] = useState(false);

    // const regionOptions = Object.entries(geo.regions).map(([regionKey, regionValue]) => (
    //   <option key={regionKey} value={regionKey}>
    //     {regionValue}
    //   </option>
    // ));

    // const selectedRegion = geo.regions[regionProps.value];

    // const districtOptions = selectedRegion ? Object.entries(geo.districts[selectedRegion]).map(([districtKey, districtValue]) => (
    //   <option key={districtKey} value={districtValue}>
    //     {districtKey}
    //   </option>
    // )) : null;

    return (

    <div className="Login">
    <div className='header'>
    <Header/>
    </div>
    <div className='Logo'>
      <Logo/>
      </div>
      <div className='content'>

<div className={styles.reg}>
    <div className={styles.reg_btns}>
        <div className={styles.reg_light_active}></div>
        <div className={styles.reg_light}></div>
    </div>

    <form className={styles.reg_form} onSubmit={onFormSubmit}>
        {!showSecondForm ? (
            <div>
                       <div className={styles.block1}>
            <input
                placeholder={t("reg_name")}
                className={styles.firstname}
              {...firstNameProps}
            />

            <input
                placeholder={t("reg_surname")}
                className={styles.lastname}
             {...lastNameProps}
            />
        </div>
        <div style={{ position: "relative" }}>
            {errors?.last_name && (
                <p
                    style={{
                        color: "red",
                        position: "absolute",
                        left: "52%",
                    }}
                >
                    {errors?.last_name?.message}
                </p>
            )}
            {errors?.first_name && (
                <p
                    style={{
                        color: "red",
                        marginTop: "10px",
                        paddingLeft: "15px",
                    }}
                >
                    {errors?.first_name?.message}
                </p>
            )}
        </div>

        <div className={styles.block2}>
            <input
                placeholder="E-mail"
                className={styles.email}
                    {...emailProps}
            />

            <input
                type="password"
                placeholder={t("reg_password")}
                className={styles.password}
                {...passwordProps}
            />
        </div>
        <div style={{ position: "relative" }}>
            {errors?.password && (
                <p
                    style={{
                        color: "red",
                        position: "absolute",
                        left: "52%",
                    }}
                >
                    {errors?.password?.message}
                </p>
            )}
            {errors?.email && (
                <p
                    style={{
                        color: "red",
                        marginTop: "10px",
                        paddingLeft: "15px",
                    }}
                >
                    {errors?.email?.message}
                </p>
            )}
        </div>
        <div className={styles.block3}>
            <input
                type="password"
                placeholder={t("reg_password_confirm")}
                className={styles.password_confirm}
                    {...passwordConfirmProps}
            />
        </div>
        <div>
            {" "}
            {errors.passwordСonfirm && (
                <p
                    style={{
                        color: "red",
                        marginTop: "10px",
                        paddingLeft: "15px",
                    }}
                >
                    {errors.passwordСonfirm.message}
                </p>
            )}
        </div>
        {!showSecondForm ?  (
                  <button
                  className={styles.reg_submit}
                  type="button"
                  onClick={() => {
                      setShowSecondForm(true); 

                  }}
              >
                  {'Далее'}
              </button>
            ) : (
              <div className={styles.block4}>
              {showSecondForm && (
                <button
                  className={styles.reg_submit}
                  type="submit"
                  onClick={() => {
                    // navigate('/')
                  }}
                >
                  {t("reg_submit")}
                </button>
              )}
            </div>
            ) 
            }
            </div>
        ) : (
            <div className="step2">
            <div className={styles.block1}>

            <select
              id="region"
              name="region"
              className={styles.form_control}

              // {...regionProps}
            >

 <option disabled value="">
          {t("reg_region")}
        </option>
        {/* {regionOptions} */}

            </select>

              <div>
                {errors?.country && (
                  <p
                    style={{
                      color: "red",
                      marginTop: "10px",
                      paddingLeft: "15px",
                    }}
                  >
                    {errors?.country?.message || "Error"}
                  </p>
                )}
              </div>
            </div>

            <div className={styles.block2}>
            <select
              id="district"
              name="district"
              {...districtProps}
              className={styles.form_control}


            >
    <option disabled value="">
    {t("reg_region_place")}
  </option>
        {/* {districtOptions} */}

            </select>
              <div>
                {errors?.city && (
                  <p
                    style={{
                      color: "red",
                      marginTop: "10px",
                      paddingLeft: "15px",
                    }}
                  >
                    {errors?.city?.message || "Error"}
                  </p>
                )}
              </div>
            </div>

            </div>
        )}

        <div className={styles.block5}>
            <p>
                {t("reg_checkReg")}{" "}
                <Link
                    to={"/login"}
                    style={{
                        textDecoration: "none",
                        color: "#7F3A85",
                        cursor: "pointer",
                    }}
                >
                    {t("reg_login")}
                </Link>
            </p>
        </div>
        <div className={styles.block6}>
            <div className={styles.round}>
                <input
                    className="checkbox"
                    type="checkbox"
                    id="checkbox"
                    {...checkBox}
                />
                <label htmlFor="checkbox"></label>
            </div>
            <p>
                я согласен с условиями{" "}
                <a
                    target="_blank"
                    style={{
                        textDecoration: "none",
                        color: "#7F3A85",
                        cursor: "pointer",
                    }}
