import React from 'react'
import { useForm } from 'react-hook-form';
import {useTranslation } from "react-i18next";


const Content = () => {
  const { t } = useTranslation();

  const {
    register,
    formState: {
        errors,
        reset,

    },
    handleSubmit,
} = useForm({mode: "onBlur"});

const onSubmit = (data) => {
  alert(JSON.stringify(data))
  reset()
}
  return (
    <div className='reg' >

      <div className='reg_btns'>
      <div className='reg_light active'></div>
      <div className='reg_light'></div>
      </div>

      <form  className='reg_form'  onSubmit={handleSubmit(onSubmit)}>
    <div className='block1'   >
    <input placeholder={t('reg_name')} className='firstname' {...register('firstName', {
          required : 'поле обязательно к заполнению'
        })} />
          <input placeholder={t('reg_surname')} className='lastname' {...register('lastName', {
          required : 'поле обязательно к заполнению'
        })}/>
    </div>
     <div className='block2'>
     <input placeholder='e-mail' className='email' {...register('email', {
          required : 'поле обязательно к заполнению',

        })}/>
            <input type='password' placeholder={t('reg_password')} className='password' {...register('password', {
          required : 'поле обязательно к заполнению',
          minLength : {
            value : 4,
            message : 'минимум 4 слов',
          }
        })}/>
     </div>
        <div className='block3'>
        <input type='password' placeholder={t('reg_password_confirm')} className='password_confirm' {...register('passwordСonfirm', {
          required : 'поле обязательно к заполнению',
          minLength : {
            value : 4,
            message : 'минимум 4 слов',
          }
        })}/>
        </div>

        <div className='block4'>
        <button className='reg_submit btn' type="submit" {...register('submit')} >{t('reg_submit')}</button>
        </div>

      </form>
    </div>
  )
}

export default Content
