import './App.css';
import { Route, Routes, useParams } from 'react-router-dom';
import Main from './pages/Main.jsx';
import { useDispatch, useSelector } from 'react-redux';
import { LOGGED_ROUTES, UNLOGGED_ROUTES } from './constants/routes';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { PublicApi } from './api/api';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { validateToken } from './api/auth';

const NotFound = () => (<>Page not found</>);

const ResetPassword = () => {
  const dispatch = useDispatch();
  const [tokenChecked, setTokenChecked] = useState('Loading');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const {token} = useParams();

  useEffect(() => {
    dispatch(validateToken({token}))
    .unwrap()
    .then((res) => {
      if (res.status === 'OK') {
        setTokenChecked(true);
      }
      else {
        setTokenChecked(false);
      }
    })
    .catch(() => setTokenChecked(false));
  }, []);
  if (tokenChecked === 'Loading') {
    return (<h1>Loading...</h1>);
  }
  if (tokenChecked === false) {
    return (<h1>Page doesn't exist</h1>);
  }
  return(
    <div>
      <label htmlFor='password'>New password</label>
      <input id="password" type="password" onChange={({target}) => setPassword(target.value)}/> 
      <label htmlFor='confirmPassword'>Confirm password</label>
      <input id="confirmPassword" type="password" onChange={({target}) => setConfirmPassword(target.value)}/>
      <button>Reset password</button>
    </div>
  );
}

function App() {
  const { isLogged, user } = useSelector(state => state.auth);
  return (
    <div className="App">   
      <Routes>
        <Route path='/' element={<Main/>} />
        {
          isLogged ?
          LOGGED_ROUTES.map((item, index) => <Route key={index} path={item.path} element={item.element} />)
          :
          UNLOGGED_ROUTES.map((item, index) => <Route key={index} path={item.path} element={item.element} />)
        }
        {

        }
        <Route path="/auth/reset-password/:token" element={<ResetPassword />}/>
        <Route path="/*" element={<NotFound />}/>
      </Routes>
      <ToastContainer/>
  </div>
  );
}

export default App;
