import { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import { RegisterView } from '../components/RegisterView';
import { setLanguage } from '../redux/slices/generalSlice';

export function Register(props) {
    let dispatch = useDispatch()
    const { language } = useSelector(state => state.general);


    const handleLanguageChange = e => {
        dispatch(setLanguage(e.target.value));
    }
    const {
        register,
        formState: { errors, isValid },
        handleSubmit,
        watch,
        getValues,
    } = useForm({mode: "onChange"});

    useEffect(() => {

        dispatch(fetchData());
    }, [selectedLanguage,dispatch]);

    function handleFormSubmit() {
        console.log('Submit!');
    }

    const emailProps = register("email", {
        required: "поле обязательно к заполнению",
        pattern: {
            value: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
            message: "напишите правильный email",
        }
    });

    const passwordProps = register("password", {
        required: "поле обязательно к заполнению",
        minLength: {
            value: 8,
            message: "8 минимум  символов",
        }
    });
    const passwordConfirmProps = register("passwordСonfirm", {
        required: "поле обязательно к заполнению",
        validate: (val) => {
            const { password } = getValues();
            if (watch("password") !== val) {
                return (
                    password === val || "пароли не совпадают"
                );
            }
        },
    })
    const firstNameProps =    register("first_name", {
        required: "поле обязательно к заполнению",
    })
    const lastNameProps =    register("last_name", {
        required: "поле обязательно к заполнению",
    })
    const checkBox = register("confirm", {
        required: true,
    })

    const [selectedRegion, setSelectedRegion] = useState(Number);

    const [selectedDistrict, setSelectedDistrict] = useState(Number);

    const handleDistrictChange = (event) => {
      setSelectedDistrict(event.target.value);
    };

    const handleRegionChange = (event) => {
      const regionValue = data.regions[event.target];
      setSelectedRegion(event.target.value);
      setSelectedDistrict('');
    };

    const getDistrictOptions = () => {
      if (selectedRegion && data.districts[selectedRegion]) {
        const districtEntries = Object.entries(data.districts[selectedRegion]);
        return districtEntries.map(([districtKey, districtValue]) => ({
          value: districtValue,
          label: districtKey
        }));
      }
      return null;
    };

    return (
        <RegisterView 
            onFormSubmit={handleSubmit(handleFormSubmit)}
            emailProps={emailProps}
            checkBox={checkBox}
            passwordProps={passwordProps}
            errors={errors}
            passwordConfirmProps={passwordConfirmProps}
            firstNameProps={firstNameProps}
            lastNameProps = {lastNameProps}
            handleLanguageChange={handleLanguageChange}
            isValid={isValid}
            {...props}
            language={language}/>

    );
}
