import styles from './ResetPass.module.scss';
import { useTranslation } from "react-i18next";
import {Link} from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import Header from '../Header';
import Logo from '../Logo/Logo';
import { useDispatch, useSelector } from 'react-redux';
import { setLanguage } from '../../redux/slices/generalSlice';
export const ResetPassConfirm = ({onFormSubmit, emailProps, passwordProps, errors,passwordConfirmProps, isValid})  => {
    let navigate = useNavigate();
    const dispatch = useDispatch();
    const { language } = useSelector(state => state.general);

    const handleLanguageChange = e => {
        dispatch(setLanguage(e.target.value));
    }
    const { t } = useTranslation();
    return (

    <div className="Login">
    <div className='header'>
    <Header/>
    </div>
    <div className='Logo'>
      <Logo/>
      </div>
      <div className='content'>
      <div className="reg">

            <div className={styles.reg_btns}>
                <div className={styles.reg_light}></div>
                <div className={styles.reg_light}></div>
                <div className={styles.reg_light_active}></div>

            </div>

            <form className={styles.reg_form} onSubmit={onFormSubmit}>
                <div className={styles.block9}>
              <h1 className={styles.restore_title}>{t('create_new_pass')}</h1>

                </div>

                <div className={styles.block2}>

                <input
                        placeholder={t('new_pass')}
                        className={styles.password_confirm}
                        {...passwordProps}
                    />



                </div>

                {errors?.password && (
                <p
                    style={{
                        color: "red",
                        position: "absolute",
                        left: "52%",
                    }}
                >
                    {errors?.password?.message}
                </p>
            )}
                            <div className={styles.block2}>

            <input
                    placeholder={t('check_pass')}
                    className={styles.password_confirm}
                    {...passwordConfirmProps}
                />



            </div>

                <div>
                </div>

                <div className={styles.block4}>
                    <button
                        className={styles.reg_submit}
                        type="submit"
                    >
                        {t("auth_newPass")}
                    </button>
                </div>

                <div className={styles.block5}>
                    <p>
                        {t("key_recall")}{" "}
                        <Link to={'/login'}
                            style={{
                                textDecoration: "none",
                                color: "#7F3A85",
                                cursor: "pointer",
                            }}

                        >
                            {t("reg_login")}
                        </Link>
                    </p>
                </div>
                <div className={styles.block6}>
                    <p>
                        {t("auth_acc")}{" "}
                        <Link to={'/register'}
                            style={{
                                textDecoration: "none",
                                color: "#7F3A85",
                                cursor: "pointer",
                            }}

                        >
                            {t("reg_submit")}
                        </Link>
                    </p>
                </div>

                <div className={styles.block7}>
                    <select
                        className={styles.select_css}
                        name="language"
                        id="language"
                        value={language}
                        onChange={handleLanguageChange}

                    >
                        <option
                            id="ky"
                            value="ky"
                        >
                            кыргызча
                        </option>

                        <option
                         id="ru"
                            value="ru"
                        >
                            русский
                        </option>
                    </select>
                </div>

            </form>
        </div>


      </div>



  </div>

    )

}
