import Header from '../components/Header'
import { useState } from 'react';
import Logo from '../components/Logo/Logo'
import '../App.css'
import {Login} from './auto/forms/Login/login'
import SelectCountry from './auto/forms/SelectCountry/SelectCountry';
import Auto from "./auto/forms/Auto/Auto";

function Auth() {

  const [step, setStep] = useState(1);
	const [userData, setUserData] = useState('')
	const toStep = (val) => {
		setStep(val);
	};


	const view = () => {
		switch (step) {
			case 1:
				return <Login toStep={toStep} />;
			case 2:
				return <SelectCountry toStep={toStep} />;
			case 3:
				return <Auto toStep={toStep} userData={userData} />;
			// case 4:
			// 	return <FormRecoveryWithNewPass toStep={toStep} />;
			default:
				return;
		}
	};

  return (
    <div className="Login">
      <div className='header'>
      <Header/>
      </div>
      <div className='Logo'>
        <Logo/>
        </div>
        <div className='content'>
        {/* <Auto/> */}
        {view()}

        </div>



    </div>
  );
}

export default Auth;
