import React, { useEffect } from 'react'
import { useTranslation } from 'react-i18next'
import styles from './MainTrainings.module.scss'
import MainHeader from '../MainHeader/MainHeader'
import { useDispatch, useSelector } from 'react-redux'
import { fetchAllTranings } from '../../api/tranings'

const MainTrainings = () => {
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const tranings = useSelector((state) => state.tranings.tranings);
    const status = useSelector((state) => state.mentors.status);
    const error = useSelector((state) => state.mentors.error);


    useEffect(() => {
      if (status === 'idle') {
        dispatch(fetchAllTranings());
      }
    }, [dispatch, status]);

    if (status === 'loading') {
      return <div>Loading mentors...</div>;
    }

    if (status === 'failed') {
      return <div>Error: {error}</div>;
    }
  return (
    <div className={styles.main_tranings}>
            <MainHeader/>
            <h1 className={styles.title}>{t('main_tranings')}</h1>
            <p className={styles.incoming}>{t('incoming_tranings')}</p>


    </div>
  )
}

export default MainTrainings
